// Pobierz dane HTML z pola concatenated_output
const inputHtml = items[0].json.concatenated_output || '';

// Przetwarzanie HTML do formatu Google Docs
let processedContent = inputHtml
  .replace(/<h2[^>]*>(.*?)<\/h2>/g, '# $1\n\n')
  .replace(/<h3[^>]*>(.*?)<\/h3>/g, '## $1\n\n')
  .replace(/<p>(.*?)<\/p>/g, '$1\n\n')
  .replace(/<strong>(.*?)<\/strong>/g, '*$1*')
  .replace(/<ol>|<\/ol>|<ul>|<\/ul>/g, '')
  .replace(/<li>(.*?)<\/li>/g, '* $1\n')
  .replace(/<a href="(.*?)">(.*?)<\/a>/g, '$2 ($1)')
  .replace(/<[^>]*>/g, ''); // Usu≈Ñ pozosta≈Çe tagi HTML

return {
  ...items[0].json,
  formattedContent: processedContent.trim()
};
