const getFallbackValue = (arr, index) => {
  const total = arr.length;

  for (let i = 0; i < total; i++) {
    if (arr[i] && i !== index && i !== index - 1 && i !== index + 1) {
      return arr[i];
    }
  }

  for (let i = 0; i < total; i++) {
    if (arr[i] && i !== index) return arr[i];
  }

  return null;
};

return [
  {
    scene_titles: items.map((item, index, arr) =>
      item?.json?.response?.text ?? getFallbackValue(arr.map(it => it?.json?.response?.text), index)
    ),
    video_urls: items.map((item, index, arr) =>
      item?.json?.data?.output?.video_url ?? getFallbackValue(arr.map(it => it?.json?.data?.output?.video_url), index)
    ),
    sound_urls: items.map((item, index, arr) =>
      item?.json?.webContentLink ?? getFallbackValue(arr.map(it => it?.json?.webContentLink), index)
    )
  }
];

